$(document).ready(function(){
    $('form').each(function(){
        $(this).bind('submit',function(e){
            e.preventDefault();
            let form = $(this);
            console.log(form);
            $.ajax({
                url:form.data('url'),
                data:new FormData(this),
                cache:false,
                async:false,
                type:'post',
                contentType:false,
                processData:false,
                success:function(data){
                    if(data.error){
                        Swal.fire({
                            icon:'error',
                            title:data.pesan,
                            timer:1500,
                            showConfirmButton:false
                        });
                    }else{
                        
                        document.location = data.url;
                    }
                }
            });
        });
    });
});